int __getMapSizeX(){
   xsSetContextPlayer(0);
   return kbGetMapXSize();
}
int __getMapSizeZ(){
   xsSetContextPlayer(0);
   return kbGetMapZSize();
}
int __getMapSizeTilesX(){
   xsSetContextPlayer(0);
   return kbGetMapXSize() / 2;
}
int __getMapSizeTilesZ(){
   xsSetContextPlayer(0);
   return kbGetMapZSize() / 2;
}
int __calculateTerrainTypeId(string name = ""){
   vector v = cOriginVector;
   int old = trGetTerrainType(v);
   int oldSub = trGetTerrainSubtype(v);
   trPaintTerrainBySubtypeName(name, 0, 0, 0, 0, false);
   int foundType = trGetTerrainType(v);
   trPaintTerrain(old, oldSub, 0, 0, 0, 0, false);
   return foundType;
}
int __calculateSubTerrainTypeId(string name = ""){
   vector v = cOriginVector;
   int old = trGetTerrainType(v);
   int oldSub = trGetTerrainSubtype(v);
   trPaintTerrainBySubtypeName(name, 0, 0, 0, 0, false);
   int foundType = trGetTerrainSubtype(v);
   trPaintTerrain(old, oldSub, 0, 0, 0, 0, false);
   return foundType;
}
float __rectangleAreaGetLowX(float x0 = 0.0, float z0 = 0.0, float x1 = 0.0, float z1 = 0.0){
   return min(x0, x1);
}
float __rectangleAreaGetLowZ(float x0 = 0.0, float z0 = 0.0, float x1 = 0.0, float z1 = 0.0){
   return min(z0, z1);
}
float __rectangleAreaGetHighX(float x0 = 0.0, float z0 = 0.0, float x1 = 0.0, float z1 = 0.0){
   return max(x0, x1);
}
float __rectangleAreaGetHighZ(float x0 = 0.0, float z0 = 0.0, float x1 = 0.0, float z1 = 0.0){
   return max(z0, z1);
}
float __clampXInsideMap(float x = 0.0){
   return max(0.0, min(x, __getMapSizeX()));
}
float __clampZInsideMap(float z = 0.0){
   return max(0.0, min(z, __getMapSizeZ()));
}
vector __roundAreaToTileVertex(float x = 0.0, float y = 0.0, float z = 0.0){
   return vector(2.0 * xsFloatToInt((x + 0.5) / 2.0), 0.0, 2.0 * xsFloatToInt((z + 0.5) / 2.0));
}
void __worldSmooth(int fromTilesX = 0, int fromTilesZ = 0, int toTilesX = 0, int toTilesZ = 0, bool outsideInfluence = false, int count = 1){
   int tilesWidth = toTilesX - fromTilesX;
   int tilesHeight = toTilesZ - fromTilesZ;
   float[] heightLowerZ = new float(1 + tilesWidth, 0.0);
   float heightLowerX = 0.0;
   vector v = cOriginVector;
   float workingHeight = 0.0;
   int mapSizeX = __getMapSizeX();
   int mapSizeZ = __getMapSizeZ();
   for(int iteration = 0; iteration < count; iteration++){
       v = vector(fromTilesX * 2.0, 0.0, max(0.0, (outsideInfluence ? fromTilesZ - 1 : fromTilesZ) * 2.0));
       for(int x = 0; x <= tilesWidth; x++){
           v.x = (x + fromTilesX) * 2.0;
           heightLowerZ[x] = trGetTerrainHeight(v);
       }
       for(int z = 0; z <= tilesHeight; z++){
           v.x = max(0.0, (outsideInfluence ? fromTilesX - 1: fromTilesX) * 2.0);
           v.z = (z + fromTilesZ) * 2.0;
           heightLowerX = trGetTerrainHeight(v);
           workingHeight = 0.0;
           for(int x = 0; x <= tilesWidth; x++){
               workingHeight = heightLowerX;
               v.x = (x + fromTilesX) * 2.0;
               v.z = (z + fromTilesZ) * 2.0;
               heightLowerX = trGetTerrainHeight(v);
               workingHeight = workingHeight + 4.0 * heightLowerX;
               workingHeight = workingHeight + heightLowerZ[x];
               heightLowerZ[x] = heightLowerX;
               v.x = min((outsideInfluence ? (x + 1 + fromTilesX) : min(x + 1 + fromTilesX, toTilesX)) * 2.0, mapSizeX);
               workingHeight = workingHeight + trGetTerrainHeight(v);
               v.x = (x + fromTilesX) * 2.0;
               v.z = min((outsideInfluence ? (z + 1 + fromTilesZ) : min(z + 1 + fromTilesZ, toTilesZ)) * 2.0, mapSizeZ);
               workingHeight = workingHeight + trGetTerrainHeight(v);
               trChangeTerrainHeight((x + fromTilesX) * 2.0, (z + fromTilesZ) * 2.0, (x + fromTilesX) * 2.0, (z + fromTilesZ) * 2.0, workingHeight / 8.0);
           }
       }
   }
}
float __worldGetSteepness(vector v = cOriginVector){
   vector vInternal = vector(v.x - 1.0, v.y, v.z - 1.0);
   float height0 = trGetTerrainHeight(vInternal);
   vInternal.x = v.x + 1.0;
   float height1 = trGetTerrainHeight(vInternal);
   vInternal.z = v.z + 1.0;
   float height2 = trGetTerrainHeight(vInternal);
   vInternal.x = v.x - 1.0;
   float height3 = trGetTerrainHeight(vInternal);
   float minHeight = min(min(height0, height1), min(height2, height3));
   float maxHeight = max(max(height0, height1), max(height2, height3));
   return maxHeight - minHeight;
}
void __paintTerrainLocation(int terrainType = 0, int terrainSubType = 0, float posX = 0.0, float posZ = 0.0){
   float lowX = 2.0 * xsFloatToInt(posX / 2.0);
   float lowZ = 2.0 * xsFloatToInt(posZ / 2.0);
   trPaintTerrain(terrainType, terrainSubType, lowX, lowZ, lowX + 2.0, lowZ + 2.0, false);
}
void __paintTerrainLocationArea(int terrainType = 0, int terrainSubType = 0, float posX = 0.0, float posY = 0.0, float posZ = 0.0){
   float lowX = 2.0 * xsFloatToInt(posX / 2.0);
   float lowZ = 2.0 * xsFloatToInt(posZ / 2.0);
   trPaintTerrain(terrainType, terrainSubType, lowX, lowZ, lowX + 2.0, lowZ + 2.0, false);
}

class __CameraParameters {
   vector position = cOriginVector;
   vector matrixX = cOriginVector;
   vector matrixY = cOriginVector;
   vector matrixZ = cOriginVector;
};
__CameraParameters __cameraParametersFromLookAt(vector lookAt = cOriginVector, float distance = 0.0, float heading = 0.0, float tilt = 0.0){
   float cameraH = degToRad(heading);
   float cameraT = degToRad(tilt);
   float cameraSinH = sin(cameraH);
   float cameraCosH = cos(cameraH);
   float cameraSinT = sin(cameraT);
   float cameraCosT = cos(cameraT);
   float cameraPosX = 0.0+lookAt.x-cameraCosH*cameraCosT*distance;
   float cameraPosY = cameraSinT*distance+lookAt.y;
   float cameraPosZ = 0.0+lookAt.z-cameraSinH*cameraCosT*distance;
   __CameraParameters cameraParameters;
   cameraParameters.position = vector(cameraPosX, cameraPosY, cameraPosZ);
   cameraParameters.matrixX = vector(cameraCosH*cameraCosT, 0.0-cameraSinT, cameraSinH*cameraCosT);
   cameraParameters.matrixY = vector(cameraCosH*cameraSinT, cameraCosT, cameraSinH*cameraSinT);
   cameraParameters.matrixZ = vector(cameraSinH, 0, 0.0-cameraCosH);
   return cameraParameters;
}
class __CameraTrack {
    int createIndex = 0;
    int count = 0;
    int[] timeArray = default;
    __CameraParameters[] cameraParametersArray = default;
    float[] fovArray = default;
    float[] tensionArray = default;
    float[] continuityArray = default;
    float[] biasArray = default;
    void addWaypoint(int time = 0, vector lookAt = cOriginVector, float distance = 0.0, float heading = 0.0, float tilt = 0.0, float fov = 0.0, float tension = 0.0, float continuity = 0.0, float bias = 0.0){
        if(count < timeArray.size()){
            timeArray[count] = time;
            cameraParametersArray[count] = __cameraParametersFromLookAt(lookAt, distance, heading, tilt);
            fovArray[count] = fov;
            tensionArray[count] = tension;
            continuityArray[count] = continuity;
            biasArray[count] = bias;
        } else {
            timeArray.add(time);
            cameraParametersArray.add(__cameraParametersFromLookAt(lookAt, distance, heading, tilt));
            fovArray.add(fov);
            tensionArray.add(tension);
            continuityArray.add(continuity);
            biasArray.add(bias);
        }
        count++;
    }
    void create(vector dest = cOriginVector, float distance = 0.0, float heading = 0.0, float tilt = 0.0, float fov = 0.0, float tension = 0.0, float continuity = 0.0, float bias = 0.0){
        count = 0;
        addWaypoint(0, dest, distance, heading, tilt, fov, tension, continuity, bias);
    }
    void play(int eventId = -1, bool blendWithCamera = false, float blendTime = 0.0){
        float duration = timeArray[count - 1];
        int trackIndex = trCameraTrackCreate("__Track_"+createIndex, duration);
        for(int i = 0; i < count; i++){
           __CameraParameters cameraParameters = cameraParametersArray[i];
           int waypointIndex = trCameraTrackAddWaypoint(trackIndex, cameraParameters.position, cameraParameters.matrixX, cameraParameters.matrixY, cameraParameters.matrixZ, fovArray[i]);
           trCameraTrackWaypointSetTime(trackIndex, waypointIndex, timeArray[i]);
           trCameraTrackWaypointSetTension(trackIndex, waypointIndex, tensionArray[i]);
           trCameraTrackWaypointSetContinuity(trackIndex, waypointIndex, continuityArray[i]);
           trCameraTrackWaypointSetBias(trackIndex, waypointIndex, biasArray[i]);
        }
        trCameraTrackLoad("__Track_"+createIndex);
        trCameraTrackPlay(-1, eventId, blendWithCamera, blendTime);
        createIndex++;
    }
};

int[] __drawXIndices(int bagSize = 1, int drawCount = 1){
   int[] bag = new int(bagSize, -1);
   for(int i = 0; i < bagSize; i++){
       bag[i] = i;
   }
   int[] choices = new int(drawCount, -1);
   int currentBagSize = bagSize;
   for(int i = 0; i < drawCount; i++){
       int choiceIndex = xsRandInt(0, currentBagSize - 1);
       choices[i] = bag[choiceIndex];
       currentBagSize--;
       bag[choiceIndex] = bag[currentBagSize];
   }
   return choices;
}

__CameraTrack __cameraTrack;

const string __UI_SYSTEM_UNIT = "Crate";
const float __UI_SYSTEM_LOOK_AT_Y = -10000.0;
const float __UI_SYSTEM_LOOK_AT_FOV = 1.0;
const float __UI_SYSTEM_LOOK_AT_Y_OBJECTS = -10100.0;
const float __UI_SYSTEM_LOOK_DISTANCE = 800.0;
const float __UI_SYSTEM_LOOK_DISTANCE_MODIFIER_PER_PLAYER = 1000.0;
const float __UI_SYSTEM_OFFSCREEN_Z = 1000.0;

int __uiSystemContextPlayer = -1;

class __UiEntry {
    float x = 0.0;
    float y = 0.0;
    float width = 0.0;
    float height = 0.0;
    string content = "";
    string rolloverName = "";
    string rolloverDescription = "";
    __Parameters parameters;
    void (int, ref __Parameters) handler = [](int pToUse = 1, ref __Parameters parametersToUse) -> void {};
    int[] clickableUnitArray = default;
    int id = -1;
    int rolloverTechId = -1;

    void destroyUnits(int p = 1){
        for (int i = 0; i < clickableUnitArray.size(); i++){
            trUnitSelectClear();
            trUnitSelectByID(clickableUnitArray[i]);
            trUnitDestroy();
        }
        clickableUnitArray.resize(0, -1);
    }
    void destroyPrompt(int p = 1, bool flip = false){
        if (trCurrentPlayer() == p){
            trWorldSpacePromptHide("__UiSystem" + id + "Flip" + flip);
        }
    }

    void show(int p = 1, vector lookAt = cOriginVector, bool flip = false, bool debugClickArea = false, bool fakeShow = false){
        if (fakeShow){
           if (trCurrentPlayer() == p){
               trWorldSpacePromptArea("__UiSystem" + id + "Flip" + flip, lookAt + vector(x, 0.0, y + __UI_SYSTEM_OFFSCREEN_Z), content, cOriginVector);
           }
           return;
        }
        if (trCurrentPlayer() == p){
            trWorldSpacePromptArea("__UiSystem" + id + "Flip" + flip, lookAt + vector(x, 0.0, y), content, cOriginVector);
        }
        if (rolloverTechId >= 0){
            trTechHideEffects(rolloverTechId, p, true);
            trTechSetStringID(rolloverTechId, p, rolloverName + "<color=0,0,0>", cXSTechEffectDisplayName);
            trTechSetStringID(rolloverTechId, p, "</color>\n" + rolloverDescription, cXSTechEffectRollover);
        }
        if (width > 0.0 && height > 0.0){
            vector pos = lookAt + vector(x, -1.0, y);
            float posZ = -0.5;
            while (posZ <= height - 0.5){
                posZ = posZ + 1.0;
                float posX = -0.5;
                while (posX <= width - 0.5){
                    posX = posX + 1.0;
                    int unit = trUnitCreateForced(__UI_SYSTEM_UNIT, pos.x + min(posX, width - 0.5) - width / 2.0, pos.y, pos.z + min(posZ, height - 0.5), 0, p);
                    trUnitSelectClear();
                    trUnitSelectByID(unit);
                    trUnitChangeProtoUnit(__UI_SYSTEM_UNIT, false, true);
                    if(rolloverTechId >= 0){
                        trRelicForce(unit, kbTechGetName(rolloverTechId));
                    }
                    if(debugClickArea == false){
                        trUnitSetShading(3, 10000);
                    }
                    clickableUnitArray.add(unit);
                }
            }
        }
    }

    void refresh(int p = 1, vector lookAt = cOriginVector, bool debugClickArea = false){
        for (int i = 0; i < clickableUnitArray.size(); i++){
            trUnitSelectClear();
            trUnitSelectByID(clickableUnitArray[i]);
            trUnitDestroy();
        }
        clickableUnitArray.resize(0, -1);
        if (width > 0.0 && height > 0.0){
            vector pos = lookAt + vector(x, -1.0, y);
            float posZ = -0.5;
            while (posZ <= height - 0.5){
                posZ = posZ + 1.0;
                float posX = -0.5;
                while (posX <= width - 0.5){
                    posX = posX + 1.0;
                    int unit = trUnitCreateForced(__UI_SYSTEM_UNIT, pos.x + min(posX, width - 0.5) - width / 2.0, pos.y, pos.z + min(posZ, height - 0.5), 0, p);
                    trUnitSelectClear();
                    trUnitSelectByID(unit);
                    trUnitChangeProtoUnit(__UI_SYSTEM_UNIT, false, true);
                    if(rolloverTechId >= 0){
                        trRelicForce(unit, kbTechGetName(rolloverTechId));
                    }
                    if(debugClickArea == false){
                        trUnitSetShading(3, 10000);
                    }
                    clickableUnitArray.add(unit);
                }
            }
        }
    }
};

__UiEntry __UI_SYSTEM_DUMMY_ENTRY;

class __UiSystem {
    int p = 1;
    bool debugClickArea = false;
    vector lookAt = cOriginVector;
    vector lookAtForCamera = cOriginVector;
    bool uiActive = false;
    __UiEntry[] uiEntryArray = default;
    __UiEntry[] uiEntryArrayToDestroy = default;
    bool flip = false;
    int[] deletableFlagsToRevert = default;
    bool disableClick = false;
    int updateTimeRequest = -1;

    void initialise(int pToUse = 1, bool debugClickAreaToUse = false){
        p = pToUse;
        debugClickArea = debugClickAreaToUse;
        lookAt = vector(0.5 * kbGetMapXSize(), __UI_SYSTEM_LOOK_AT_Y_OBJECTS - pToUse * __UI_SYSTEM_LOOK_DISTANCE_MODIFIER_PER_PLAYER, 0.5 * kbGetMapZSize());
        lookAtForCamera = vector(0.5 * kbGetMapXSize(), __UI_SYSTEM_LOOK_AT_Y - pToUse * __UI_SYSTEM_LOOK_DISTANCE_MODIFIER_PER_PLAYER, 0.5 * kbGetMapZSize());
    }

    void enter(bool disableClickToUse = false){
        if(uiActive == false){
            uiActive = true;
            for(int i = 0; i < cNumberProtoUnits; i++){
               bool deletable = kbPlayerGetProtoStatFlag(p, i, cProtoUnitFlagDeleteable);
               if(deletable){
                   deletableFlagsToRevert.add(i);
                   trProtoUnitSetFlag(p, kbProtoUnitGetName(i), "Deleteable", false);
               }
            }
            trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Deleteable", true);
            int tempToSelect = trUnitCreateForced(__UI_SYSTEM_UNIT, lookAt.x, lookAt.y, lookAt.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(tempToSelect);
            if (trCurrentPlayer() == p){
                trUnitGameSelect(true);
            }
            trUnitDestroy();
            if (trCurrentPlayer() == p){
                __cameraTrack.create(lookAtForCamera, __UI_SYSTEM_LOOK_DISTANCE, 90.0, 90.0, __UI_SYSTEM_LOOK_AT_FOV);
                __cameraTrack.addWaypoint(1000000000, lookAtForCamera, __UI_SYSTEM_LOOK_DISTANCE, 90.0, 90.0, __UI_SYSTEM_LOOK_AT_FOV);
                __cameraTrack.play(-1);
            }
        }
        if(disableClickToUse != disableClick){
           trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Deleteable", disableClick);
           trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "ForceDeleteable", disableClick);
           disableClick = disableClickToUse;
        }
        updateTimeRequest = xsGetTimeMS() + 1;
    }

    void exit(bool normaliseCamera = false){
        if(uiActive){
            int tempToSelect = trUnitCreateForced(__UI_SYSTEM_UNIT, lookAt.x, lookAt.y, lookAt.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(tempToSelect);
            if (trCurrentPlayer() == p){
                trUnitGameSelect(true);
            }
            trUnitDestroy();
            uiActive = false;
            for(int i = 0; i < deletableFlagsToRevert.size(); i++){
                trProtoUnitSetFlag(p, kbProtoUnitGetName(deletableFlagsToRevert[i]), "Deleteable", true);
            }
            deletableFlagsToRevert.clear();
            if (normaliseCamera){
               if (trCurrentPlayer() == p){
                   __cameraTrack.create(lookAtForCamera, __UI_SYSTEM_LOOK_DISTANCE, 45.0, 90.0, 40.0);
                   __cameraTrack.addWaypoint(1, lookAtForCamera, __UI_SYSTEM_LOOK_DISTANCE, 45.0, 90.0, 40.0);
                   __cameraTrack.play(-1, true, 0);
               }
            }
        }
        for (int i = 0; i < uiEntryArrayToDestroy.size(); i++){
            __UiEntry entry = uiEntryArrayToDestroy[i];
            entry.destroyPrompt(p, !flip);
        }
        uiEntryArrayToDestroy.resize(0);
        if(uiEntryArray.size() > 0){
            for (int i = 0; i < uiEntryArray.size(); i++){
                __UiEntry entry = uiEntryArray[i];
                entry.destroyUnits(p);
                entry.destroyPrompt(p, flip);
            }
            uiEntryArray.resize(0);
            updateTimeRequest = -1;
            flip = !flip;
        }
    }

    void clear(){
        if(uiEntryArray.size() > 0){
            for (int i = 0; i < uiEntryArray.size(); i++){
                __UiEntry entry = uiEntryArray[i];
                entry.destroyUnits(p);
                uiEntryArrayToDestroy.add(entry);
            }
            uiEntryArray.resize(0);
            flip = !flip;
            updateTimeRequest = xsGetTimeMS() + 1;
        }
    }

    void addDisplay(float x = 0.0, float y = 0.0, string content = ""){
        __UiEntry entry;
        entry.x = x;
        entry.y = y;
        entry.content = content;
        entry.id = uiEntryArray.size();
        entry.show(p, lookAt, flip, debugClickArea, true);
        uiEntryArray.add(entry);
    }

    void addClickable(float x = 0.0, float y = 0.0, float width = 0.0, float height = 0.0, string content = "", int rolloverTechId = -1, string rolloverName = "", string rolloverDescription = "", ref __Parameters parameters,
            void(int, ref __Parameters) handler = [](int pToUse = 1, ref __Parameters parametersToUse) -> void {}){
        __UiEntry entry;
        entry.x = x;
        entry.y = y;
        entry.content = content;
        entry.rolloverName = rolloverName;
        entry.rolloverDescription = rolloverDescription;
        entry.parameters = parameters;
        entry.id = uiEntryArray.size();
        entry.rolloverTechId = rolloverTechId;
        entry.width = width;
        entry.height = height;
        entry.handler = handler;
        entry.show(p, lookAt, flip, debugClickArea, true);
        uiEntryArray.add(entry);
    }

    void process(){
        if (uiActive && updateTimeRequest >= 0 && xsGetTimeMS() >= updateTimeRequest){
           updateTimeRequest = -1;
           for(int i = 0; i < uiEntryArrayToDestroy.size(); i++){
               __UiEntry entry = uiEntryArrayToDestroy[i];
               entry.destroyPrompt(p, !flip);
           }
           for(int i = 0; i < uiEntryArray.size(); i++){
               __UiEntry entry = uiEntryArray[i];
               entry.show(p, lookAt, flip, debugClickArea, false);
           }
           uiEntryArrayToDestroy.resize(0);
       }
    }
    __UiEntry processThrottled(){
        if (uiActive && disableClick == false){
            int clickedEntryIndex = -1;
            bool validClick = true;
            for (int i = 0; i < uiEntryArray.size(); i++){
                __UiEntry entry = uiEntryArray[i];
                int[] clickableUnitArray = entry.clickableUnitArray;
                for (int j = 0; j < clickableUnitArray.size(); j++){
                    trUnitSelectClear();
                    trUnitSelectByID(clickableUnitArray[j]);
                    if (trUnitAlive() == false){
                        if (clickedEntryIndex == -1){
                            clickedEntryIndex = i;
                        }
                        else if (clickedEntryIndex != i){
                            validClick = false;
                        }
                    }
                }
            }
            if (validClick == false){
                for (int i = 0; i < uiEntryArray.size(); i++){
                    __UiEntry entry = uiEntryArray[i];
                    entry.refresh(p, lookAt, debugClickArea);
                    uiEntryArray[i] = entry;
                }
            } else if (clickedEntryIndex >= 0){
                for (int i = 0; i < uiEntryArray.size(); i++){
                    __UiEntry entry = uiEntryArray[i];
                    entry.destroyUnits(p);
                    uiEntryArrayToDestroy.add(entry);
                }
                __UiEntry entry = uiEntryArray[clickedEntryIndex];
                uiEntryArray.resize(0);
                flip = !flip;
                updateTimeRequest = xsGetTimeMS() + 1;
                return entry;
            }
            if (trCurrentPlayer() == p){
                trExecuteConsoleCommand("uiDeleteSelectedUnit(true)");
            }
        }
        return __UI_SYSTEM_DUMMY_ENTRY;
    }
};

__UiSystem[] __uiSystemArray = default;
__UiSystem __uiSystemBeingAddedTo;

void __initialiseUiSystem(bool usingRollovers = false, int pollDelay = 0, bool debugClickArea = false){
    __uiSystemArray.resize(cNumberPlayers + 1);
    for (int p = 1; p <= cNumberPlayers; p++){
        __UiSystem uiSystem = __uiSystemArray[p];
        uiSystem.initialise(p, debugClickArea);
        __uiSystemArray[p] = uiSystem;
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Collideable", false);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "DontMarkExtraFog", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "VisibleUnderFog", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "ForceToNature", false);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "TieToGround", false);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Selectable", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Deleteable", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "FadeInOnBuild", debugClickArea == false);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Relic", usingRollovers);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "ForceDeleteable", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "Invulnerable", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "InvulnerableToAreaDamage", true);
        trProtoUnitSetFlag(p, __UI_SYSTEM_UNIT, "HasLOS", true);
        trProtoUnitSetUnitType(p, __UI_SYSTEM_UNIT, "Building", true);
        trModifyProtounitData(__UI_SYSTEM_UNIT, p, cXSProtoEffectBuildPoints, 100000, cXSRelativityAssign);
        trModifyProtounitData(__UI_SYSTEM_UNIT, p, cXSProtoEffectLOS, 0.0, cXSRelativityAssign);
    }
    __scheduler.add(0, false, [](int iteration = 0) -> bool {
        for (int p = 1; p <= cNumberPlayers; p++){
            __UiSystem system = __uiSystemArray[p];
            system.process();
            __uiSystemArray[p] = system;
        }
        return true;
    });
    __scheduler.add(pollDelay, false, [](int iteration = 0) -> bool {
        for (int p = 1; p <= cNumberPlayers; p++){
            __UiSystem system = __uiSystemArray[p];
            __UiEntry entry = system.processThrottled();
            __uiSystemArray[p] = system;
            entry.handler(p, entry.parameters);
        }
        return true;
    });
}

const string __ONSCREEN_UI_UNIT = "Crate";
const string __ONSCREEN_UI_UNIT2 = "CrateSmall";
const float __ONSCREEN_UI_SMALL_SCALE_MULTIPLIER = 1.355;
const float __ONSCREEN_UI_LOOK_DISTANCE = 57.3;
const float __ONSCREEN_UI_OFFSCREEN_Z = 1000.0;

int[] __onscreenUiRelicTechArray = default;
int __onscreenUiSystemContextPlayer = -1;

class __OnscreenUiEntry {
    string unitType = "";
    bool clickable = false;
    bool hoverable = false;
    float x = 0.0;
    float y = 0.0;
    float width = 0.0;
    float height = 0.0;
    string content = "";
    string rolloverName = "";
    string rolloverDescription = "";
    __Parameters parameters;
    void (int, ref __Parameters) handler = [](int pToUse = 1, ref __Parameters parametersToUse) -> void {};
    int[] clickableUnitArray = default;
    int id = -1;
    int rolloverId = -1;
    void destroyUnits(int p = 1){
        for (int i = 0; i < clickableUnitArray.size(); i++){
            trUnitSelectClear();
            trUnitSelectByID(clickableUnitArray[i]);
            trUnitDestroy();
        }
        clickableUnitArray.resize(0, -1);
    }
    void destroyPrompt(int p = 1, int page = 0){
        if (trCurrentPlayer() == p){
            trWorldSpacePromptHide("__OnscreenUiSystem" + id + "Page" + page);
        }
    }
    void show(int p = 1, vector lookAt = cOriginVector, vector xAxisDirection = cOriginVector, vector yAxisDirection = cOriginVector, float lookAtHeading = 0.0, float lookAtTilt = 0.0, 
           int page = 0, bool fakeShow = false, bool debug = false, bool disableClick = false, vector cameraPosition = cOriginVector){
        if (fakeShow){
            if (trCurrentPlayer() == p && content != ""){
                trWorldSpacePromptArea("__OnscreenUiSystem" + id + "Page" + page, lookAt + xAxisDirection * x + yAxisDirection * (y + __ONSCREEN_UI_OFFSCREEN_Z), content, cOriginVector);
            }
            return;
        }
        if (trCurrentPlayer() == p && content != ""){
            vector promptLocation = lookAt + xAxisDirection * x + yAxisDirection * y;;
            vector directionVector = xsVectorNormalize(promptLocation - cameraPosition);
            trWorldSpacePromptArea("__OnscreenUiSystem" + id + "Page" + page, cameraPosition + directionVector * 100000000.0, content, cOriginVector);
        }
        if (hoverable){
            trTechHideEffects(__onscreenUiRelicTechArray[rolloverId], p, true);
            trTechSetStringID(__onscreenUiRelicTechArray[rolloverId], p, rolloverName + "<color=0,0,0>", cXSTechEffectDisplayName);
            trTechSetStringID(__onscreenUiRelicTechArray[rolloverId], p, "</color>\n" + rolloverDescription, cXSTechEffectRollover);
        }
        if (clickable || hoverable){
            vector pos = lookAt + xAxisDirection * x + yAxisDirection * (y + height / 2.0);
            int unit = trUnitCreateForced(unitType, pos.x, pos.y, pos.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(unit);
            if (!debug){
                trUnitChangeProtoUnit(unitType, false, true);
                trUnitSetShading(3, 1000.0);
            }
            if (hoverable){
                trUnitSetScale(__ONSCREEN_UI_SMALL_SCALE_MULTIPLIER * height, 0.001, __ONSCREEN_UI_SMALL_SCALE_MULTIPLIER * width);
            } else {
                trUnitSetScale(height, 0.001, width);
            }
            trUnitRotateZ(lookAtTilt - 90, true);
            trUnitRotateY(-lookAtHeading - 180, true);
            if (!clickable || disableClick){
                trUnitSetFlag(cUnitFlagIsUnbuilding, true);
            }
            trRelicForce(unit, hoverable ? kbTechGetName(__onscreenUiRelicTechArray[rolloverId]) : "");
            clickableUnitArray.add(unit);
        }
    }
    void refresh(int p = 1, vector lookAt = cOriginVector, vector xAxisDirection = cOriginVector, vector yAxisDirection = cOriginVector, float lookAtHeading = 0.0, float lookAtTilt = 0.0, 
            bool debug = false, bool disableClick = false){
        for (int i = 0; i < clickableUnitArray.size(); i++){
            trUnitSelectClear();
            trUnitSelectByID(clickableUnitArray[i]);
            trUnitDestroy();
        }
        clickableUnitArray.resize(0, -1);
        if (clickable || hoverable){
            vector pos = lookAt + xAxisDirection * x + yAxisDirection * (y + height / 2.0);
            int unit = trUnitCreateForced(unitType, pos.x, pos.y, pos.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(unit);
            if (!debug){
                trUnitChangeProtoUnit(unitType, false, true);
                trUnitSetShading(3, 1000.0);
            }
            if (hoverable){
                trUnitSetScale(__ONSCREEN_UI_SMALL_SCALE_MULTIPLIER * height, 0.001, __ONSCREEN_UI_SMALL_SCALE_MULTIPLIER * width);
            } else {
                trUnitSetScale(height, 0.001, width);
            }
            trUnitRotateZ(lookAtTilt - 90, true);
            trUnitRotateY(-lookAtHeading - 180, true);
            if (!clickable || disableClick){
                trUnitSetFlag(cUnitFlagIsUnbuilding, true);
            }
            trRelicForce(unit, hoverable ? kbTechGetName(__onscreenUiRelicTechArray[rolloverId]) : "");
            clickableUnitArray.add(unit);
        }
    }
};

__OnscreenUiEntry __DUMMY_ONSCREEN_UI_ENTRY;

class __OnscreenUiSystemPage {
    int page = 0;
    int realShow = -1;
    int delayDestroy = -1;
    __OnscreenUiEntry[] onscreenUiEntryArray = default;
    bool process(int p = 1, vector lookAt = cOriginVector, vector xAxisDirection = cOriginVector, vector yAxisDirection = cOriginVector, float lookAtHeading = 0.0, float lookAtTilt = 0.0, 
            bool debug = false, bool disableClick = false, vector cameraPosition = cOriginVector){
        bool isShowingToReturn = false;
        if(realShow >= 0 && xsGetTimeMS() > realShow){
            isShowingToReturn = true;
            realShow = -1;
            for(int i = 0; i < onscreenUiEntryArray.size(); i++){
                __OnscreenUiEntry entry = onscreenUiEntryArray[i];
                entry.show(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, page, false, debug, disableClick, cameraPosition);
            }
        }
        if(delayDestroy >= 0 && xsGetTimeMS() > delayDestroy){
            delayDestroy = -1;
            for(int i = 0; i < onscreenUiEntryArray.size(); i++){
                __OnscreenUiEntry entry = onscreenUiEntryArray[i];
                entry.destroyPrompt(p, page);
            }
            onscreenUiEntryArray.resize(0);
        }
        return isShowingToReturn;
    }
    void requestDestroy(int p = 1, bool fromExit = false){
        for(int i = 0; i < onscreenUiEntryArray.size(); i++){
            __OnscreenUiEntry entry = onscreenUiEntryArray[i];
            entry.destroyUnits(p);
            if(fromExit){
                entry.destroyPrompt(p, page);
            }
        }
        if(fromExit){
            realShow = -1;
        } else {
            delayDestroy = xsGetTimeMS();
        }
    }
};

class __OnscreenUiSystem {
    int p = 1;
    bool debug = false;
    vector lookAt = cOriginVector;
    vector lookAtForCamera = cOriginVector;
    vector cameraPosition = cOriginVector;
    float lookAtDistance = __ONSCREEN_UI_LOOK_DISTANCE;
    float lookAtHeading = 90.0;
    float lookAtTilt = 90.0;
    float lookAtFov = 1.0;
    vector xAxisDirection = vector(1.0, 0.0, 0.0);
    vector yAxisDirection = vector(0.0, 0.0, 1.0);
    bool uiActive = false;
    bool disableClick = false;
    int[] deletableFlagsToRevert = default;
    bool[] selectableFlagsToRevert = default;
    int rolloverCount = 0;
    __OnscreenUiSystemPage page0;
    __OnscreenUiSystemPage page1;
    __OnscreenUiSystemPage page2;
    int addPage = 0;
    int showingPage = 0;
    void initialise(int pToUse = 1, bool debugToUse = false){
        p = pToUse;
        debug = debugToUse;
        page0.page = 0;
        page1.page = 1;
        page2.page = 2;
    }
    void setCameraPosition(vector dest = cOriginVector, float distance = 0.0, float heading = 0.0, float tilt = 0.0, float fov = 40.0){
        lookAtForCamera = dest;
        lookAtDistance = distance;
        lookAtHeading = heading;
        lookAtTilt = tilt;
        lookAtFov = fov;
        float cameraH = degToRad(heading);
        float cameraT = degToRad(tilt);
        float cameraSinH = sin(cameraH);
        float cameraCosH = cos(cameraH);
        float cameraSinT = sin(cameraT);
        float cameraCosT = cos(cameraT);
        float cameraPosX = 0.0 + dest.x - cameraCosH * cameraCosT * distance;
        float cameraPosY = cameraSinT * distance + dest.y;
        float cameraPosZ = 0.0 + dest.z - cameraSinH * cameraCosT * distance;
        cameraPosition = vector(cameraPosX, cameraPosY, cameraPosZ);
        float canvasDistance = (tan(degToRad(0.5)) * __ONSCREEN_UI_LOOK_DISTANCE) / tan(degToRad(0.5 * fov));
        float canvasDistanceFromLookPoint = -canvasDistance + distance;
        float canvasPosX = 0.0 + dest.x - cameraCosH * cameraCosT * canvasDistanceFromLookPoint;
        float canvasPosY = cameraSinT * canvasDistanceFromLookPoint + dest.y;
        float canvasPosZ = 0.0 + dest.z - cameraSinH * cameraCosT * canvasDistanceFromLookPoint;
        lookAt = vector(canvasPosX, canvasPosY, canvasPosZ);
        xAxisDirection = vector(cameraSinH, 0.0, -cameraCosH);
        yAxisDirection = vector(cameraSinT * cameraCosH, cameraCosT, cameraSinT * cameraSinH);
        if (uiActive && trCurrentPlayer() == p){
            __cameraTrack.create(lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, lookAtFov);
            __cameraTrack.addWaypoint(1000000000, lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, lookAtFov);
            __cameraTrack.play(-1);
        }
    }
    void enter(bool disableClickToUse = false){
        if (uiActive == false){
            uiActive = true;
            for(int i = 0; i < cNumberProtoUnits; i++){
               bool deletable = kbPlayerGetProtoStatFlag(p, i, cProtoUnitFlagDeleteable);
               if(deletable){
                   deletableFlagsToRevert.add(i);
                   trProtoUnitSetFlag(p, kbProtoUnitGetName(i), "Deleteable", false);
               }
            }
            if(trCurrentPlayer() == p){
               selectableFlagsToRevert.resize(cNumberProtoUnits * (cNumberPlayers + 1), false);
               int offset = 0;
               for(int q = 0; q <= cNumberPlayers; q++){
                   for(int i = 0; i < cNumberProtoUnits; i++){
                       bool selectable = kbPlayerGetProtoStatFlag(q, i, cProtoUnitFlagSelectable);
                       selectableFlagsToRevert[i + offset] = selectable;
                       if(selectable){
                           trProtoUnitSetFlag(q, kbProtoUnitGetName(i), "Selectable", false);
                       }
                   }
                   offset = offset + cNumberProtoUnits;
               }
            }
            trProtoUnitSetFlag(p, __ONSCREEN_UI_UNIT, "Deleteable", true);
            trProtoUnitSetFlag(p, __ONSCREEN_UI_UNIT2, "Deleteable", true);
            trProtoUnitSetFlag(p, __ONSCREEN_UI_UNIT, "Selectable", true);
            trProtoUnitSetFlag(p, __ONSCREEN_UI_UNIT2, "Selectable", true);
            int tempToSelect = trUnitCreateForced(__UI_SYSTEM_UNIT, lookAt.x, lookAt.y, lookAt.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(tempToSelect);
            if (trCurrentPlayer() == p){
                trUnitGameSelect(true);
            }
            trUnitDestroy();
            if (trCurrentPlayer() == p){
                __cameraTrack.create(lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, lookAtFov);
                __cameraTrack.addWaypoint(1000000000, lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, lookAtFov);
                __cameraTrack.play(-1);
            }
        }
        disableClick = disableClickToUse;
        if(addPage == 0){
            addPage = 1;
            page1.realShow = xsGetTimeMS();
        } else if(addPage == 1) {
            addPage = 2;
            page2.realShow = xsGetTimeMS();
        } else {
            addPage = 0;
            page0.realShow = xsGetTimeMS();
        }
    }
    void exit(bool normaliseCamera = false, float fov = 40.0){
        if (uiActive == true){
            uiActive = false;
            int tempToSelect = trUnitCreateForced(__ONSCREEN_UI_UNIT, lookAt.x, lookAt.y, lookAt.z, 0, p);
            trUnitSelectClear();
            trUnitSelectByID(tempToSelect);
            if (trCurrentPlayer() == p){
                trUnitGameSelect(true);
            }
            trUnitDestroy();
            for(int i = 0; i < deletableFlagsToRevert.size(); i++){
                trProtoUnitSetFlag(p, kbProtoUnitGetName(deletableFlagsToRevert[i]), "Deleteable", true);
            }
            deletableFlagsToRevert.clear();
            if(trCurrentPlayer() == p){
               int offset = 0;
               for(int q = 0; q <= cNumberPlayers; q++){
                   for(int i = 0; i < cNumberProtoUnits; i++){
                       bool selectable = selectableFlagsToRevert[i + offset];
                       if(selectable){
                           trProtoUnitSetFlag(q, kbProtoUnitGetName(i), "Selectable", true);
                       }
                   }
                   offset = offset + cNumberProtoUnits;
               }
               selectableFlagsToRevert.clear();
            }
            if (trCurrentPlayer() == p){
                if (normaliseCamera){
                    __cameraTrack.create(lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, fov);
                    __cameraTrack.addWaypoint(1, lookAtForCamera, lookAtDistance, lookAtHeading, lookAtTilt, fov);
                    __cameraTrack.play(-1, true, 0);
                }
            }
            page0.requestDestroy(p, true);
            page1.requestDestroy(p, true);
            page2.requestDestroy(p, true);
        }
    }
    void clear(){
        if(showingPage == 0) {
            page0.requestDestroy(p, false);
        } else if(addPage == 1) {
            page1.requestDestroy(p, false);
        } else {
            page2.requestDestroy(p, false);
        }
        rolloverCount = 0;
    }
    void addDisplay(float x = 0.0, float y = 0.0, string content = ""){
        __OnscreenUiEntry entry;
        entry.unitType = __ONSCREEN_UI_UNIT;
        entry.clickable = false;
        entry.hoverable = false;
        entry.x = x;
        entry.y = y;
        entry.content = content;
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.size();
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.size();
        } else {
            entry.id = page2.onscreenUiEntryArray.size();
        }
        entry.show(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, addPage, true, debug, disableClick, cameraPosition);
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.add(entry);
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.add(entry);
        } else {
            entry.id = page2.onscreenUiEntryArray.add(entry);
        }
    }
    void addDisplayWithHover(float x = 0.0, float y = 0.0, float width = 0.0, float height = 0.0, string content = "", string rolloverName = "", string rolloverDescription = ""){
        __OnscreenUiEntry entry;
        entry.unitType = __ONSCREEN_UI_UNIT2;
        entry.clickable = false;
        entry.hoverable = true;
        entry.x = x;
        entry.y = y;
        entry.width = width;
        entry.height = height;
        entry.content = content;
        entry.rolloverName = rolloverName;
        entry.rolloverDescription = rolloverDescription;
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.size();
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.size();
        } else {
            entry.id = page2.onscreenUiEntryArray.size();
        }
        entry.rolloverId = rolloverCount;
        rolloverCount++;
        entry.show(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, addPage, true, debug, disableClick, cameraPosition);
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.add(entry);
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.add(entry);
        } else {
            entry.id = page2.onscreenUiEntryArray.add(entry);
        }
    }
    void addClickable(float x = 0.0, float y = 0.0, float width = 0.0, float height = 0.0, string content = "", ref __Parameters parameters,
            void(int, ref __Parameters) handler = [](int pToUse = 1, ref __Parameters parametersToUse) -> void {}){
        __OnscreenUiEntry entry;
        entry.unitType = __ONSCREEN_UI_UNIT;
        entry.clickable = true;
        entry.hoverable = false;
        entry.x = x;
        entry.y = y;
        entry.width = width;
        entry.height = height;
        entry.content = content;
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.size();
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.size();
        } else {
            entry.id = page2.onscreenUiEntryArray.size();
        }
        entry.parameters = parameters;
        entry.handler = handler;
        entry.show(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, addPage, true, debug, disableClick, cameraPosition);
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.add(entry);
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.add(entry);
        } else {
            entry.id = page2.onscreenUiEntryArray.add(entry);
        }
    }
    void addClickableWithHover(float x = 0.0, float y = 0.0, float width = 0.0, float height = 0.0, string content = "", string rolloverName = "", string rolloverDescription = "", ref __Parameters parameters,
            void(int, ref __Parameters) handler = [](int pToUse = 1, ref __Parameters parametersToUse) -> void {}){
        __OnscreenUiEntry entry;
        entry.unitType = __ONSCREEN_UI_UNIT2;
        entry.clickable = true;
        entry.hoverable = true;
        entry.x = x;
        entry.y = y;
        entry.width = width;
        entry.height = height;
        entry.content = content;
        entry.rolloverName = rolloverName;
        entry.rolloverDescription = rolloverDescription;
        entry.parameters = parameters;
        entry.handler = handler;
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.size();
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.size();
        } else {
            entry.id = page2.onscreenUiEntryArray.size();
        }
        entry.rolloverId = rolloverCount;
        rolloverCount++;
        entry.show(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, addPage, true, debug, disableClick, cameraPosition);
        if(addPage == 0){
            entry.id = page0.onscreenUiEntryArray.add(entry);
        } else if(addPage == 1) {
            entry.id = page1.onscreenUiEntryArray.add(entry);
        } else {
            entry.id = page2.onscreenUiEntryArray.add(entry);
        }
    }
    __OnscreenUiEntry process(){
        if(page0.process(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, debug, disableClick, cameraPosition)){
            showingPage = 0;
        }
        if(page1.process(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, debug, disableClick, cameraPosition)){
            showingPage = 1;
        }
        if(page2.process(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, debug, disableClick, cameraPosition)){
            showingPage = 2;
        }
        if (uiActive){
            int clickedEntryIndex = -1;
            bool validClick = true;
            __OnscreenUiEntry[] onscreenUiEntryArray = showingPage == 0 ? page0.onscreenUiEntryArray : (showingPage == 1 ? page1.onscreenUiEntryArray : page2.onscreenUiEntryArray);
            for (int i = 0; i < onscreenUiEntryArray.size(); i++){
                __OnscreenUiEntry entry = onscreenUiEntryArray[i];
                int[] clickableUnitArray = entry.clickableUnitArray;
                for (int j = 0; j < clickableUnitArray.size(); j++){
                    trUnitSelectClear();
                    trUnitSelectByID(clickableUnitArray[j]);
                    if (trUnitAlive() == false){
                        if (clickedEntryIndex == -1){
                            clickedEntryIndex = i;
                        } else if (clickedEntryIndex != i){
                            validClick = false;
                        }
                    }
                }
            }
            if (validClick == false){
                for (int i = 0; i < onscreenUiEntryArray.size(); i++){
                    __OnscreenUiEntry entry = onscreenUiEntryArray[i];
                    entry.refresh(p, lookAt, xAxisDirection, yAxisDirection, lookAtHeading, lookAtTilt, debug, disableClick);
                    onscreenUiEntryArray[i] = entry;
                }
            } else if (clickedEntryIndex >= 0){
                if(showingPage == 0) {
                    page0.requestDestroy(p, false);
                } else if(addPage == 1) {
                    page1.requestDestroy(p, false);
                } else {
                    page2.requestDestroy(p, false);
                }
                rolloverCount = 0;
                __OnscreenUiEntry entry = onscreenUiEntryArray[clickedEntryIndex];
                return entry;
            }
            if (trCurrentPlayer() == p){
                trExecuteConsoleCommand("uiDeleteSelectedUnit(true)");
            }
        }
        return __DUMMY_ONSCREEN_UI_ENTRY;
    }
};

__OnscreenUiSystem[] __onscreenUiSystemArray = default;

void __initialiseOnscreenUiSystem(bool allRelicTechs = false, bool debug = false){
    if(allRelicTechs){
        for (int i = 0; i < cNumberTechs; i++){
            if (xsStringStartsWith(kbTechGetName(i), "Relic", true)){
                __onscreenUiRelicTechArray.add(i);
            }
        }
    }
    __onscreenUiSystemArray.resize(cNumberPlayers + 1);
    string[] systemUnits = new string(2, "");
    systemUnits[0] = __ONSCREEN_UI_UNIT;
    systemUnits[1] = __ONSCREEN_UI_UNIT2;
    for (int p = 1; p <= cNumberPlayers; p++){
        __OnscreenUiSystem uiSystem = __onscreenUiSystemArray[p];
        uiSystem.initialise(p, debug);
        uiSystem.setCameraPosition(vector(0.5 * kbGetMapXSize(), -10100.0, 0.5 * kbGetMapZSize()), 100.0, 45.0, 89.0, 40.0);
        __onscreenUiSystemArray[p] = uiSystem;
        for (int i = 0; i < systemUnits.size(); i++){
            string systemUnit = systemUnits[i];
            trProtoUnitSetFlag(p, systemUnit, "Collideable", false);
            trProtoUnitSetFlag(p, systemUnit, "ForceDeleteable", true);
            trProtoUnitSetFlag(p, systemUnit, "Invulnerable", true);
            trProtoUnitSetFlag(p, systemUnit, "InvulnerableToAreaDamage", true);
            trProtoUnitSetFlag(p, systemUnit, "DontMarkExtraFog", true);
            trProtoUnitSetFlag(p, systemUnit, "VisibleUnderFog", true);
            trProtoUnitSetFlag(p, systemUnit, "ForceToNature", false);
            trProtoUnitSetFlag(p, systemUnit, "TieToGround", false);
            trProtoUnitSetFlag(p, systemUnit, "Selectable", true);
            trProtoUnitSetFlag(p, systemUnit, "Deleteable", true);
            trProtoUnitSetFlag(p, systemUnit, "FadeInOnBuild", !debug);
            trProtoUnitSetFlag(p, systemUnit, "HasLOS", true);
            trProtoUnitSetFlag(p, systemUnit, "ObscuredByUnits", true);
            trProtoUnitSetUnitType(p, systemUnit, "Building", true);
            if (p != trCurrentPlayer()){
                trProtoUnitSetFlag(p, systemUnit, "OnlyInEditor", true);
            }
            trModifyProtounitData(systemUnit, p, cXSProtoEffectBuildPoints, 100000, cXSRelativityAssign);
            trModifyProtounitData(systemUnit, p, cXSProtoEffectLOS, debug ? 5.0 : 0.0, cXSRelativityAssign);
        }
        trProtoUnitSetFlag(p, __ONSCREEN_UI_UNIT2, "Relic", true);
    }
    __scheduler.add(0, false, [](int iterations = 1) -> bool {
        for(int p = 1; p <= cNumberPlayers; p++){
            __OnscreenUiSystem system = __onscreenUiSystemArray[p];
            __OnscreenUiEntry entry = system.process();
            __onscreenUiSystemArray[p] = system;
            entry.handler(p, entry.parameters);
        }
        return true;
    });
}

