string ntlReplaceText(string text = "", string toReplace = "", string replacement = ""){
   int indexFound = xsStringFindFirst(text, toReplace, 0, true);
   if(indexFound < 0){
       return text;
   }
   string toReturn = xsStringSubstring(text, 0, indexFound - 1) + replacement;
   while(indexFound >= 0){
       int newIndexFound = xsStringFindFirst(text, toReplace, indexFound + xsStringLength(toReplace), true);
       if(newIndexFound >= 0){
           toReturn = toReturn + xsStringSubstring(text, indexFound + xsStringLength(toReplace), newIndexFound - 1) + replacement;
           indexFound = newIndexFound;
       } else {
           toReturn = toReturn + xsStringSubstring(text, indexFound + xsStringLength(toReplace), xsStringLength(text) - 1);
           indexFound = -1;
       }
   }
   return toReturn;
}
string ntlToForwardSlash(string text = ""){
   return ntlReplaceText(text, xsStringSubstring("\ ", 0, 0), "/");
}
string ntlWrapText(string text = "", int lineLimit = 0){
   int textLength = xsStringLength(text);
   if(textLength <= lineLimit){
       return text;
   }
   string slash = xsStringCharAt("\ ", 0);
   string toBuild = "";
   int startIndex = 0;
   int lastSpace = -1;
   int currentIndex = 0;
   while(currentIndex < textLength){
       string character = xsStringCharAt(text, currentIndex);
       if(character == slash && currentIndex < (textLength - 1) && xsStringCharAt(text, currentIndex + 1) == "n"){
           toBuild = toBuild + xsStringSubstring(text, startIndex, currentIndex);
           currentIndex++;
           startIndex = currentIndex;
           lastSpace = -1;
       } else if(character == " "){
           if(currentIndex - startIndex <= lineLimit){
               lastSpace = currentIndex;
               currentIndex++;
           } else {
               if(lastSpace < 0){
                   toBuild = toBuild + xsStringSubstring(text, startIndex, currentIndex - 1);
                   startIndex = currentIndex + 1;
               } else {
                   toBuild = toBuild + xsStringSubstring(text, startIndex, lastSpace - 1);
                   startIndex = lastSpace + 1;
               }
               toBuild = toBuild + "\n";
               lastSpace = -1;
               currentIndex = startIndex;
           }
       } else {
           currentIndex++;
       }
   }
   if(lastSpace >= 0 && (textLength - startIndex) > lineLimit){
       toBuild = toBuild + xsStringSubstring(text, startIndex, lastSpace - 1);
       toBuild = toBuild + "\n";
       startIndex = lastSpace + 1;
   }
   toBuild = toBuild + xsStringSubstring(text, startIndex, textLength - 1);
   return toBuild;
}
int ntlCountTextLines(string text = ""){
   int total = 1;
   int index = xsStringFindFirst(text, "\n", 0, true);
   while(index >= 0){
       total++;
       index = xsStringFindFirst(text, "\n", index + 2, true);
   }
   return total;
}
string ntlPadTextToTextCount(string text = "", string padText = "", int wantedCount = 0){
   int count = 0;
   int textLength = xsStringLength(text);
   int padLength = xsStringLength(padText);
   int startIndex = 0;
   int found = xsStringFindFirst(text, padText, 0, true);
   while(found >= 0){
       count++;
       found = xsStringFindFirst(text, padText, found + padLength, true);
   }
   if(count >= wantedCount){
       return text;
   }
   string padding = "";
   for(int i = count; i < wantedCount; i++){
       padding = padding + padText;
   }
   return text + padding;
}

